//   flexDirection: "column",
//   alignItems: "center",
//   gap: "2em",
//   padding: "2em",
// };
const resultsContainerStyle = {
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  gap: "1em",
  width: "100%",
};

const noResultsStyle = {
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  color: "#0073e6",
  fontSize: "18px",
  fontWeight: "bold",
  marginTop: "20px",
  padding: "10px 20px",
  borderRadius: "15px",
  border: "1px solid #0073e6",
  backgroundColor: "#f0f4ff",
  boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
  width: "100%",
  textAlign: "center",
};
