import { useState } from "react";
import * as S from "./styles";
import { Summary, Arrow } from "./styles"; // Ensure the correct path
import axios from "axios";

const PersonDetails = ({ person }) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const [editablePerson, setEditablePerson] = useState(person);
  const [errorMessage, setErrorMessage] = useState("");

  const toggleExpand = () => {
    setIsExpanded(!isExpanded);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditablePerson((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleNoticeChange = (e, noticeIndex, noticeFieldIndex) => {
    const { value } = e.target;
    setEditablePerson((prev) => {
      const updatedNotices = [...prev.notices];
      updatedNotices[noticeIndex][noticeFieldIndex] = value;
      return {
        ...prev,
        notices: updatedNotices,
      };
    });
  };

  const handleSave = async () => {
    try {
      // Send the updated data to the backend
      const response = await axios.post('http://127.0.0.1:5000/update', editablePerson);

      if (response.data.success) {
        setErrorMessage("");
        // Update the parent component state or trigger a re-fetch if necessary
      } else {
        setErrorMessage("Nu s-a modificat");
      }
    } catch (error) {
      console.error("There was an error!", error);
      setErrorMessage("Nu s-a modificat");
    }
  };

  // Safely access user_data and other fields
  const userData = editablePerson.user_data || editablePerson;
  const applicationDates = editablePerson.application_dates || [];
  const instructionGroups = editablePerson.instruction_groups || [];
  const notices = editablePerson.notices || [];

  return (
    <S.PersonDetails>
      <Summary>
        <div className="info">
          <div className="info-item">{userData.username || "N/A"}</div>
          <div className="info-item">{userData.idnp || "N/A"}</div>
          <div className="info-item">{userData.phone_number || "N/A"}</div>
          <div className="info-item">{userData.email || "N/A"}</div>
        </div>
        <Arrow onClick={toggleExpand}>
          {isExpanded ? "▲" : "▼"}
        </Arrow>
      </Summary>
      {isExpanded && (
        <div className="detailsStyle">
          <div>
            <b>Data aplicării:</b>
            <input
              type="text"
              name="application_dates"
              value={applicationDates.join(", ")}
              onChange={(e) => {
                const dates = e.target.value.split(",").map(date => date.trim());
                setEditablePerson((prev) => ({
                  ...prev,
                  application_dates: dates,
                }));
              }}
            />
          </div>
          <div>
            <b>Data nașterii:</b>
            <input
              type="text"
              name="bday"
              value={userData.bday || "N/A"}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <b>Vârsta:</b>
            <input
              type="text"
              name="age"
              value={userData.age || "N/A"}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <b>Localitate:</b>
            <input
              type="text"
              name="location"
              value={userData.location || "N/A"}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <b>Programul la care aplică:</b>
            <input
              type="text"
              name="program"
              value={userData.program || "N/A"}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <b>Persoana la care a apelat:</b>
            <input
              type="text"
              name="operator"
              value={userData.operator || "N/A"}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <b>Data confirmării recepționării formularului:</b>
            <input
              type="text"
              name="confirmation_date"
              value={userData.confirmation_date || "N/A"}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <b>Comentariu în urma discuției:</b>
            <input
              type="text"
              name="comment"
              value={userData.comment || "N/A"}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <b>Statut în urma dicuției:</b>
            <input
              type="text"
              name="status"
              value={userData.status || "N/A"}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <b>Grupa de instruire în care a fost invitat:</b>
            <input
              type="text"
              name="instruction_groups"
              value={instructionGroups.join(", ")}
              onChange={(e) => {
                const groups = e.target.value.split(",").map(group => group.trim());
                setEditablePerson((prev) => ({
                  ...prev,
                  instruction_groups: groups,
                }));
              }}
            />
          </div>
          <div>
            <b>Certificat obținut:</b>
            <input
              type="text"
              name="certificate"
              value={userData.certificate || "N/A"}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <b>Aplicat la finanțare:</b>
            <input
              type="text"
              name="finance"
              value={userData.finance || "N/A"}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <b>Statut în urma evaluării planului de afaceri:</b>
            <input
              type="text"
              name="final_status"
              value={userData.final_status || "N/A"}
              onChange={handleInputChange}
            />
          </div>
          <div>
            <b>Excluderea din baza de date:</b>
            <input
              type="text"
              name="exclusion"
              value={userData.exclusion || "N/A"}
              onChange={handleInputChange}
            />
          </div>
          <div className="phoneStyle">
            <b>Înștiințări:</b>
            {notices.length > 0 ? (
              <table className="noticeTable">
                <thead>
                  <tr>
                    <th>Notice</th>
                    <th>Phone Calls</th>
                    <th>Email Calls</th>
                    <th>SMS Calls</th>
                    <th>Viber Calls</th>
                    <th>Response</th>
                  </tr>
                </thead>
                <tbody>
                  {notices.map((notice, index) => (
                    <tr key={index}>
                      <td>{index + 1}</td>
                      <td>
                        <input
                          type="text"
                          name={`notice_${index}_phone_call`}
                          value={notice[0] || "N/A"}
                          onChange={(e) => handleNoticeChange(e, index, 0)}
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          name={`notice_${index}_email_call`}
                          value={notice[1] || "N/A"}
                          onChange={(e) => handleNoticeChange(e, index, 1)}
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          name={`notice_${index}_sms_call`}
                          value={notice[2] || "N/A"}
                          onChange={(e) => handleNoticeChange(e, index, 2)}
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          name={`notice_${index}_viber_call`}
                          value={notice[3] || "N/A"}
                          onChange={(e) => handleNoticeChange(e, index, 3)}
                        />
                      </td>
                      <td>
                        <input
                          type="text"
                          name={`notice_${index}_response`}
                          value={notice[4]}
                          onChange={(e) => handleNoticeChange(e, index, 4)}
                        />
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              "N/A"
            )}
          </div>
          <div className="button-container">
            <button onClick={handleSave}>Salveaza Modificarile</button>
          </div>
          {errorMessage && <div className="error">{errorMessage}</div>}
        </div>
      )}
    </S.PersonDetails>
  );
};

export default PersonDetails;
