import SearchForm from "../SearchForm";
import * as S from "./styles";
import { useState } from "react";
import PersonDetails from "../PersonDetails";
import axios from "axios";
import { saveAs } from "file-saver";

const Main = () => {
  const [searchResult, setSearchResult] = useState(null);
  const [noResults, setNoResults] = useState(false);

  const handleSearch = async (formValues) => {
    console.log("Form Values:", formValues);
    try {
      const response = await axios.post('http://localhost:5000/search', formValues);
      setSearchResult(response.data);
      setNoResults(false);
    } catch (error) {
      console.error("There was an error!", error);
      setSearchResult(null);
      setNoResults(true);
    }

    // Save form values to JSON file
    const json = JSON.stringify(formValues, null, 2);
    const blob = new Blob([json], { type: "application/json" });
    saveAs(blob, "search_results.json");
  };

  const handleClear = () => {
    setSearchResult(null);
    setNoResults(false);
  };

  return (
    <S.Main>
      <div>
        <SearchForm onSubmit={handleSearch} />
        <S.Results>
          {noResults && <div className="noResultsStyle">Nu s-a gÄƒsit nimic</div>}
          {searchResult && (
            <div>
              <button onClick={handleClear} className="clearButtonStyle">
                Clear
              </button>
              <PersonDetails person={searchResult} />
            </div>
          )}
        </S.Results>
      </div>
    </S.Main>
  );
};

export default Main;
