import * as XLSX from "xlsx";
import { saveAs } from "file-saver";
import React, { useEffect } from 'react';

const ExcelExportButton = ({ data }) => {
  useEffect(() => {
    console.log("Data passed to ExcelExportButton:", data); // Log the data
  }, [data]);

  const exportToExcel = () => {
    const wb = XLSX.utils.book_new();
    const wsData = [];

    // Define the headers
    const headers = [
      "Nume Prenume", "Data aplicării", "Data aplicării repetat", "IDNP", "Data nașterii", "Vârsta", "Localitate", "Telefon",
      "E-mail", "Programul la care aplică", "Persoana care a apelat", "Data confirmării recepționării formularului / Data apelului", "Comentariu în urma discuției",
      "Statut în urma discuției", "TELEFON", "E-MAIL", "SMS", "VIBER", "Răspuns", "Grupa de instruire în care a fost invitat",
      "A doua înștiințare TELEFON", "A doua înștiințare E-MAIL", "A doua înștiințare SMS", "A doua înștiințare VIBER", "A doua înștiințare Răspuns",
      "A treia înștiințare TELEFON", "A treia înștiințare E-MAIL", "A treia înștiințare SMS", "A treia înștiințare VIBER", "A treia înștiințare Răspuns",
      "Participare la instruire", "Certificat obținut", "Aplicat la finanțare", "Statut în urma evaluării planului de afaceri", "Excluderea din baza de date"
    ];

    // Initialize the worksheet with headers
    wsData.push(headers);

    // Map through data to create rows as per the provided template
    data.forEach((person, personIndex) => {
      const applicationDates = person.application_dates || [];
      const instructionGroups = person.instruction_groups || [];
      const notices = person.notices || [];

      // Create a row for each application date
      applicationDates.forEach((date, index) => {
        const row = [
          person.user_data.username || 'N/A',
          date || 'N/A',
          applicationDates[index + 1] || 'N/A',
          person.user_data.idnp || 'N/A',
          person.user_data.bday || 'N/A',
          person.user_data.age || 'N/A',
          person.user_data.loc_name || 'N/A',
          person.user_data.phone_number || 'N/A',
          person.user_data.email || 'N/A',
          person.user_data.program_name || 'N/A',
          person.user_data.operator || 'N/A',
          person.user_data.confirmation_date || 'N/A',
          person.user_data.comment || 'N/A',
          person.user_data.status || 'N/A',
          notices[0] ? notices[0][0] || 'N/A' : 'N/A',
          notices[0] ? notices[0][1] || 'N/A' : 'N/A',
          notices[0] ? notices[0][2] || 'N/A' : 'N/A',
          notices[0] ? notices[0][3] || 'N/A' : 'N/A',
          notices[0] ? notices[0][4] || 'N/A' : 'N/A',
          instructionGroups[index] || 'N/A',
          notices[1] ? notices[1][0] || 'N/A' : 'N/A',
          notices[1] ? notices[1][1] || 'N/A' : 'N/A',
          notices[1] ? notices[1][2] || 'N/A' : 'N/A',
          notices[1] ? notices[1][3] || 'N/A' : 'N/A',
          notices[1] ? notices[1][4] || 'N/A' : 'N/A',
          notices[2] ? notices[2][0] || 'N/A' : 'N/A',
          notices[2] ? notices[2][1] || 'N/A' : 'N/A',
          notices[2] ? notices[2][2] || 'N/A' : 'N/A',
          notices[2] ? notices[2][3] || 'N/A' : 'N/A',
          notices[2] ? notices[2][4] || 'N/A' : 'N/A',
          person.instruction_groups.join(", ") || 'N/A',
          person.user_data.certificate || 'N/A',
          person.user_data.finance || 'N/A',
          person.user_data.final_status || 'N/A',
          person.user_data.exclusion ? 'Exclus' : 'N/A'
        ];
        wsData.push(row);
      });
    });

    // Debugging: Log the wsData array to ensure it has the correct data
    console.log(wsData);

    // Create worksheet
    const ws = XLSX.utils.aoa_to_sheet(wsData);

    // Adjust column width for readability
    const columnWidths = headers.map(header => ({ wch: header.length + 5 }));
    ws["!cols"] = columnWidths;

    // Append worksheet to workbook
    XLSX.utils.book_append_sheet(wb, ws, 'PersonDetails');

    // Write workbook and save as file
    const wbout = XLSX.write(wb, { bookType: "xlsx", type: "array" });
    const blob = new Blob([wbout], { type: "application/octet-stream" });

    saveAs(blob, "PersonDetails.xlsx");
  };

  return (
    <div style={buttonContainerStyle}>
      <button onClick={exportToExcel} style={buttonStyle}>
        Export to Excel
      </button>
    </div>
  );
};

const buttonContainerStyle = {
  backgroundColor: "#213b8b",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  height: "42vh",
};

const buttonStyle = {
  padding: "10px 20px",
  borderRadius: "25px",
  border: "1px solid #0073e6",
  backgroundColor: "#0073e6",
  color: "#fff",
  fontSize: "16px",
  fontWeight: "bold",
  cursor: "pointer",
  transition: "background-color 0.3s, box-shadow 0.3s",
};

export default ExcelExportButton;
